import React, { useState } from 'react';

import { validateEmail } from '../../';

const TagsInput = ({ label, id, name, placeholder, error, onChange, defaultTags, emailValidation }) => {
  const [value, setValue] = useState('');
  const [tags, setTags] = useState(defaultTags ? defaultTags : []);
  const [isActive, setIsActive] = useState(false);

  const changeHandler = (e) => {
    setValue(e.target.value);
    onChange(name, tags);
  }

  const removeTag = (tag) => {
    const arr = tags.filter(t => t !== tag);
    setTags(arr);
    onChange(name, arr);
  }

  const updateTagsHandler = (e) => {
    e.preventDefault();

    // Add tags if input is not empty and if input value is not comma
    if(e.target.value !== '' && e.target.value !== ',') {

      if(e.key === ',') {

        const newTag = value.trim().split(',')[0];
        if(emailValidation) {
          if(!tags.includes(newTag) && newTag !== '' && validateEmail(newTag)) {
            const arr = [...tags, newTag];
            setTags(arr);
            onChange(name, arr);
          }
        }else {
          if(!tags.includes(newTag) && newTag !== '') {
            const arr = [...tags, newTag];
            setTags(arr);
            onChange(name, arr);
          }
        }
        setValue('');

      } else if(e.key === 'Enter') {

        const newTag = value.trim();
        if(emailValidation) {
          if(!tags.includes(newTag) && newTag !== '' && validateEmail(newTag)) {
            const arr = [...tags, newTag];
            setTags(arr);
            onChange(name, arr);
          }
        }else {
          if(!tags.includes(newTag) && newTag !== '') {
            const arr = [...tags, newTag];
            setTags(arr);
            onChange(name, arr);
          }
        }
        setValue('');

      }

    }

    // Remove tags if backspace is pressed
    if(e.key === 'Backspace' && tags.length > 0) {
      const copyOfTags = [...tags];
      copyOfTags.pop();
      setTags(copyOfTags);
      onChange(name, copyOfTags);
    }
  }

  const focusHandler = () => {
    setIsActive(true);
  }

  const blurHandler = () => {
    setIsActive(false);
  }

  return(
    <div className={!isActive ? "tags-input" : "tags-input active"}>
      {label && <label htmlFor={id ? id : name}>{label}</label>}
      <div className="tags-input__wrapper">
        <div className="tags-input__tags">
          {tags.map((tag, i) => 
            <div key={i} className="tag">
              {tag} <span onClick={() => removeTag(tag)}><i className="fas fa-times-circle"></i></span>
            </div>
          )}
          <input 
            type="text"
            placeholder={placeholder}
            name={name}
            id={id ? id : name}
            value={value}
            onChange={changeHandler}
            autoComplete="off"
            onKeyUp={updateTagsHandler}
            onKeyDown={(e) => e.key === 'Enter' && e.preventDefault()}
            onKeyPress={(e) => e.key === 'Enter' && e.preventDefault()}
            onFocus={focusHandler}
            onBlur={blurHandler}
          />
        </div>
      </div>
      {error && <div className="error">{error}</div>}
    </div>
  );
}

export default TagsInput;
