import React from "react";
import { Tabs, Tab } from "react-bootstrap-tabs";

function EditorTabContain() {
  return (
    <div style={{minWidth}}>
      <section>
       
        <Tabs
          style={{ border: "2px solid green" }}
          headerStyle={{ fontWeight: "bold" }}
          activeHeaderStyle={{ color: "blue" }}
          contentStyle={{ backgroundColor: "lightgoldenrodyellow" }}
        >
          <Tab label="Container style">
            Tabs container has a green border
            <br />
            <br />
            <pre>&lt;Tabs style={"{{border:'2px solid green'}}"}&gt;</pre>
          </Tab>
          <Tab label="Header style">
            All the tab headers have a bold font, the active tab header has a
            blue font
            <br />
            <br />
            <pre>
              &lt;Tabs headerStyle={"{{fontWeight: 'bold'}}"} activeHeaderStyle=
              {"{{color: 'blue'}}"}&gt;
            </pre>
          </Tab>
          <Tab label="Tab Style" style={{ color: "green" }}>
            The tab contents have a yellow background and this one specifically
            has green aligned text
            <br />
            <br />
            <pre>
              &lt;Tabs contentStyle=
              {"{{backgroundColor:'lightgoldenrodyellow'}}"}&gt;
              <br />
              &nbsp;&nbsp;&nbsp;&nbsp;&lt;Tab style={
                "{{textAlign: 'right'}}"
              }{" "}
              /&gt;
              <br />
              &lt;/Tabs&gt;
            </pre>
          </Tab>
        </Tabs>
      </section>
    </div>
  );
}

export default EditorTabContain;
