import React from "react";

const writerInitialValues = [
  { label: "Writer1", value: "Writer1" },
  { label: "Writer2", value: "Writer2" },
  { label: "Writer3", value: "Writer3" }
];

const createOption = label => ({
 
  value: label
});

export default class TagsInput extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      //value: [],
      value: writerInitialValues,
      focused: false,
      writerInputValue: ""
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleInputKeyDown = this.handleInputKeyDown.bind(this);
    this.handleEditItem = this.handleEditItem.bind(this);
    this.handleBlur = this.handleBlur.bind(this);
    this.handleRemoveItem = this.handleRemoveItem.bind(this);
  }

  render() {
    const styles = {
      keyWordContainer: {
        border: "1px solid #ddd",
        padding: "5px",
        borderRadius: "5px"
      },

      keywordItems: {
        display: "inline-block",
        padding: "2px",
        border: "1px solid black",
        fontFamily: "Helvetica, sans-serif",
        //borderRadius: "5px",
        marginRight: "5px",
        cursor: "pointer"
      },

      keywordListitem: {
        display: "inline-block",
        marginRight:10
      },

      keywordInput: {
      
        border: "",
        fontSize: "14px",
        fontFamily: "Helvetica, sans-serif"
      }
    };
    return (
      <div>

     
      <label>
        <ul style={styles.keywordContainer}>
          {this.state.value.map((item, i) => (
            <li style={styles.keywordListitem}>
              <div key={i} style={styles.keywordItems}>
                <span onClick={this.handleEditItem(i)}>{item.label} </span>
                <span onClick={this.handleRemoveItem(i)}> |x </span>
              </div>
            </li>
          ))}
           </ul>
      </label>
          <input
            style={styles.keywordInput}
            value={this.state.inputValue}
            onChange={this.handleInputChange}
            onKeyDown={this.handleInputKeyDown}
            onBlur={this.handleBlur}
          />
       
      </div>
    );
  }

  

  handleRemoveItem(index) {
    return () => {
      this.setState(state => ({
        value: state.value.filter((item, i) => i !== index)
      }));
    };
  }

  handleBlur(evt) {
    const { value } = evt.target;

    if (value !== "") {
      this.setState(state => ({
        value: [...state.value, createOption(value)],
        inputValue: ""
      }));
    }

    console.log(this.state.value);
  }

  handleInputChange(evt) {
    this.setState({ inputValue: evt.target.value });
  }

  handleInputKeyDown(evt) {
    if (evt.keyCode === 13) {
      const { value } = evt.target;

      if (value !== "") {
        this.setState(state => ({
          value: [...state.value, createOption(value)],
          inputValue: ""
        }));
      }
    }

    if (evt.keyCode === 9) {
      const { value } = evt.target;

      if (value !== "") {
        this.setState(state => ({
          value: [...state.value, createOption(value)],
          inputValue: ""
        }));
      }
    }

    if (
      this.state.value.length &&
      evt.keyCode === 8 &&
      !this.state.inputValue.length
    ) {
      this.setState(state => ({
        value: state.value.slice(0, state.value.length - 1)
      }));
    }

    console.log(this.state.value);
  }

  handleEditItem(index) {
    return () => {
      let it = this.state.value.filter((item, i) => i === index);
      console.log(it);
      this.setState(state => ({
        value: state.value.filter((item, i) => i !== index),
        inputValue: it[0].label
      }));
      //need to change to text box or open a new textbox
    };
  }
}
