import React, { Component } from "react";
import { Link, Redirect } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { Button, Form, Checkbox, Radio } from "semantic-ui-react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { Container, Row, Nav, Navbar,Modal } from "react-bootstrap";

import Footer from "../Components/Footer";

export default class Admin extends Component {
  constructor(props) {
    super(props);

    const token = localStorage.getItem("token");
    let loggedIn = true;
    if (token == null) {
      loggedIn = false;
    }
    this.state = {
      loggedIn,
      showHide: false,
    };
  }

  handleModalShowHide() {
    this.setState({ showHide: !this.state.showHide });
  }

  logoutButtonFunction = (e) => {
    localStorage.removeItem("token");

    console.log("cliasdk");
    return this.props.history.push("/");
  };

 

  render() {
    const { loggedIn } = this.state;
    if (!loggedIn) {
      return <Redirect to="/" />;
    }
    return (
      <Router>
        <header>
          <Navbar bg="dark" variant="dark" collapseOnSelect>
            <Container>
              <Navbar.Brand>
                <h1 style={{ color: "white" }}>Nuntium</h1>
              </Navbar.Brand>
             
              <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="ml-auto">
                  <div onClick={ this.handleModalShowHide()}>
                    <h3 style={{ color: "white" }}>
                      Log-out <i class="fas fa-sign-out-alt"></i>
                    </h3>
                  </div>

                  <Modal show={this.state.showHide}>
                    <Modal.Header
                      closeButton
                      onClick={() => this.handleModalShowHide()}
                    >
                      <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>

                    <Modal.Body>
                      Woohoo, you're reading this text in a modal!
                    </Modal.Body>

                    <Modal.Footer>
                      <Button
                        variant="secondary"
                        onClick={() => this.handleModalShowHide()}
                      >
                        Close
                      </Button>

                      <Button
                        variant="primary"
                        onClick={() => this.handleModalShowHide()}
                      >
                        Save Changes
                      </Button>
                    </Modal.Footer>
                  </Modal>
                </Nav>
              </Navbar.Collapse>
            </Container>
          </Navbar>
        </header>
        <main className="py-3">
          <Container>
            <div>
              hello <i class="fas fa-sign-out-alt"></i>
            </div>
          </Container>
        </main>
        <Footer />
      </Router>
    );
  }
}
