import React, { Component } from "react";
import { Link, Redirect } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { Form, Checkbox, Radio } from "semantic-ui-react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import { Container, Row, Nav, Navbar, Modal, Button } from "react-bootstrap";
import SplitPane from "react-split-pane";

import Footer from "../Components/Footer";

export default class Admin extends Component {
  constructor(props) {
    super(props);

    this.handleShowlogoutmodal = this.handleShowlogoutmodal.bind(this);
    this.handleCloselogoutmodal = this.handleCloselogoutmodal.bind(this);

    const token = localStorage.getItem("token");
    let loggedIn = true;
    if (token == null) {
      loggedIn = false;
    }
    this.state = {
      loggedIn,
      show: false,
    };
  }

  handleCloselogoutmodal() {
    this.setState({ show: false });
  }

  handleShowlogoutmodal() {
    this.setState({ show: true });
  }

  logoutButtonFunction = (e) => {
    localStorage.removeItem("token");

    console.log("cliasdk");
    return this.props.history.push("/");
  };

  render() {
    const { loggedIn } = this.state;
    if (!loggedIn) {
      return <Redirect to="/" />;
    }
    return (
      <Router>
        <header>
          <Navbar bg="dark" variant="dark" collapseOnSelect>
            <Container>
              <Navbar.Brand>
                <h1 style={{ color: "white" }}>Nuntium</h1>
              </Navbar.Brand>
              <Navbar.Toggle aria-controls="basic-navbar-nav" />
              <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="ml-auto">
                  <div onClick={this.handleShowlogoutmodal}>
                    <h3 style={{ color: "white" }}>
                      Log-out <i class="fas fa-sign-out-alt"></i>
                    </h3>
                  </div>
                </Nav>
              </Navbar.Collapse>
            </Container>
          </Navbar>
        </header>
        <main className="py-3">
          <Container>
            <div>
              <SplitPane
                split="vertical"
                minSize={50}
                maxSize={100}
                defaultSize={100}
              >
                <div><h1>Welcome</h1></div>
                <div><h1>Welcome</h1></div>
              </SplitPane>
              
            </div>

            <Modal show={this.state.show} onHide={this.handleCloselogoutmodal}>
              <Modal.Header variant="dark" closeButton>
                <Modal.Title>
                  <h1 style={{}}>Logout </h1>
                </Modal.Title>
              </Modal.Header>
              <Modal.Body>Do you wanna logeout</Modal.Body>
              <Modal.Footer>
                <Button variant="danger" onClick={this.logoutButtonFunction}>
                  Yes
                </Button>
              </Modal.Footer>
            </Modal>
          </Container>
        </main>
        <Footer />
      </Router>
    );
  }
}
