import React, { Component } from "react";
import { Container, Row, Nav, Navbar, Modal, Col } from "react-bootstrap";
import { Button, Form,  } from "semantic-ui-react";
import { Typography, Select ,Input} from "antd";
import { TweenOneGroup } from "rc-tween-one";
import { PlusOutlined } from "@ant-design/icons";
import { MinusCircleOutlined } from "@ant-design/icons";

const { Title } = Typography;
const { TextArea } = Input;
const { Option, OptGroup } = Select;

export class AddNewsTab extends Component {
  constructor() {
    super();

    this.state = {
      show: false,
      addNewsCategories: null,
      addnewsHeadline: "",
      newsData: "",
      newsImage:'',
        //keywordTags is of input
        keywordTags: [],
        keywordInputVisible: false,
        keywordInputValue: "",
    
      
    };

    
    
    this.handleShowlogoutmodal = this.handleShowlogoutmodal.bind(this);
    this.handleCloselogoutmodal = this.handleCloselogoutmodal.bind(this);

    this.handleaddNewsCategoriesChange = this.handleaddNewsCategoriesChange.bind(this);
    this.handleHeadlineChange = this.handleHeadlineChange.bind(this);
    this.handleNewsDataChange = this.handleNewsDataChange.bind(this);
    this.handleInputImageChange= this.handleInputImageChange.bind(this);
  }

 
  //keyword input tag
  keywordTagHandleClose = (removedTag) => {
    const keywordTags = this.state.keywordTags.filter(
      (tag) => tag !== removedTag
    );
    console.log(keywordTags);
    this.setState({ keywordTags });
  };

  keywordkeywordTagShowInput = () => {
    this.setState({ keywordInputVisible: true }, () => this.input.focus());
  };

  keywordTagHandleInputChange = (e) => {
    this.setState({ keywordInputValue: e.target.value });
  };

  keywordTagHandleInputConfirm = () => {
    const { keywordInputValue } = this.state;
    let { keywordTags } = this.state;
    if (keywordInputValue && keywordTags.indexOf(keywordInputValue) === -1) {
      keywordTags = [...keywordTags, keywordInputValue];
    }
    console.log(keywordTags);
    this.setState({
      keywordTags,
      keywordInputVisible: false,
      keywordInputValue: "",
    });
  };
  


  handleCloselogoutmodal() {
    this.setState({ show: true });
  }

  handleShowlogoutmodal() {
    this.setState({ show: false });
  }

  handleaddNewsCategoriesChange(event) {
    this.setState({
      addNewsCategories: event.target.value,
    });
    console.log(this.state.addNewsCategories);
  }

  handleHeadlineChange(event) {
    this.setState({
      addnewsHeadline: event.target.value,
    });
 
  }

  handleNewsDataChange (event) {
    this.setState({
      newsData: event.target.value,
    });
    
  }
  
  handleInputImageChange(event) {
    this.setState({
      newsImage: URL.createObjectURL(event.target.files[0])
    });
    
  }

  

  submit() {
    console.log(this.state.addNewsCategories);
  }

  render() {
    const { addNewsCategories, addnewsHeadline ,newsData , newsImage} = this.state;
    const { keywordTags, keywordInputVisible, keywordInputValue } = this.state;
    const keywordTagChild = keywordTags.map(this.saveInputReForMap);

    
    return (
      <div>
        <div style={{ marginRight: 20 }}>
          <div class="card " style={{ borderColor: '"#b33939' }}>
            <div class="card-header">
              <h1 style={{ color: "#b33939" }}>Add News</h1>
            </div>
            <div class="card-body">
              {/**contain starts fromm here */}

              <Container>
                <Form>
                  <div style={{ marginTop: "3vh" }}>
                    {/**Add category */}
                    <h3 style={{ marginBottom: "3vh" }}>Add category :</h3>

                    <select
                      className="form-control"
                      name="city"
                      style={{ width: "20vh", fontSize: 16 }}
                      onChange={this.handleaddNewsCategoriesChange}
                    >
                      <option selected>Select Category</option>
                      <option value="Business">Business</option>
                      <option value="Entertainment">Entertainment</option>
                      <option value="General">General</option>
                      <option value="Health">Health</option>
                      <option value="Science">Science</option>
                      <option value="Sport">Sport</option>
                      <option value="Technology">Technology</option>
                    </select>
                  </div>

                  {/**headlines */}
                  <div style={{ marginTop: "3vh" }}>
                    <h3 style={{ marginBottom: "3vh" }}>Headline :</h3>

                    <Form.Input
                      iconPosition="left"
                      placeholder="Enter Headline "
                      value={this.state.addnewsHeadline}
                      onChange={this.handleHeadlineChange}
                    />
                  </div>

                  {/**news  */}
                  <div style={{ marginTop: "3vh" }}>
                    <h3 style={{ marginBottom: "3vh" }}>News :</h3>
                    <TextArea
                      onChange={this.handleNewsDataChange}
                      placeholder="Enter newsData"
                      autoSize={{ minRows: 4, maxRows: 7 }}
                    />
                  </div>

                  {/** photo  */}
                  <div style={{ marginTop: "3vh" }}>
                    <h3 style={{ marginBottom: "3vh" }}>Photo :</h3>
                    <input type="file" onChange={this.handleInputImageChange}/>
                    <img style={{maxWidth:'20vh'}} src={this.state.newsImage}/>
                  </div>
                  {/**tag input */}
                  <div style={{ marginTop: "3vh" }}>
                    <h3 style={{ marginBottom: "3vh" }}>Keywords :</h3>
                    
                    {/**https://www.npmjs.com/package/react-input-tags */}
                   
                    <div style={{ marginBottom: 16 }}>
                                  <TweenOneGroup
                                    enter={{
                                      scale: 0,
                                      opacity: 1,
                                      type: "from",
                                      duration: 100,
                                      onComplete: (e) => {
                                        e.target.style = "";
                                      },
                                    }}
                                    leave={{
                                      opacity: 0,
                                      width: 0,
                                      scale: 0,
                                      duration: 200,
                                    }}
                                    appear={false}
                                  >
                                    {keywordTagChild}
                                  </TweenOneGroup>
                                </div>
                                {keywordInputVisible && (
                                  <TextArea1
                                    ref={this.saveInputRefSaveInputRef}
                                    type="text"
                                    autoSize={{ minRows: 1, maxRows: 7 }}
                                    value={keywordInputValue}
                                    onChange={this.keywordTagHandleInputChange}
                                    onBlur={this.keywordTagHandleInputConfirm}
                                    onPressEnter={
                                      this.keywordTagHandleInputConfirm
                                    }
                                  />
                                )}
                                {!keywordInputVisible && (
                                  <Title
                                    onClick={this.keywordkeywordTagShowInput}
                                    style={{ width: 300 }}
                                  >
                                    <Title level={4}>
                                      Enter Keywords{" "}
                                      
                                    </Title>
                                  </Title>
                                )}

      <div>

 </div>

          </div>
                 
                  

                
                  <Button
                    negative
                    onClick={this.handleShowlogoutmodal}
                    style={{ marginTop: "80px", marginLeft: "70%" }}
                  >
                    Submit
                  </Button>

                  {/**model container starts from here */}
                  <Modal
                    show={this.state.show}
                    onHide={this.handleCloselogoutmodal}
                  >
                    <Modal.Body>
                      <h1 variant="dark">
                        {addNewsCategories} {addnewsHeadline} {newsData} 
                        <img style={{maxWidth:'10vh'}} src={this.state.newsImage}/>
            
                      </h1>
                    </Modal.Body>
                    <Modal.Footer>
                      <Button>Yes</Button>
                    </Modal.Footer>
                  </Modal>
                </Form>
              </Container>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AddNewsTab;
