}], {cancelable: false});
    }
       


    
    render() {
       

        const { loggedIn } = this.state
        if (!loggedIn) {
            return <Redirect to='/' />
        }
        return (
            <Router>
            <header>
      <Navbar bg="dark" variant="dark" collapseOnSelect>
        <Container>
          <Navbar.Brand ><h1 style={{color:'white'}}>Nuntium</h1></Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ml-auto">
            <div onClick={this.logoutAlert } ><h3  style={{color:'white'}} >Log-out <i class="fas fa-sign-out-alt"></i></h3></div> 

            
